package com.example.apprent.ui.main_page;import android.content.Intent;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.fragment.app.Fragment;import com.example.apprent.R;import com.example.apprent.data.notifications.NotificationHelper;import com.example.apprent.ui.main_activity.MainActivity;import com.example.apprent.ui.main_activity.MainActivityVM;public class MainFragment extends Fragment {    private NotificationHelper mNotificationHelper;    private MainActivityVM mainActivityVM;    private Bundle args;    @Override    public void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        mNotificationHelper = new NotificationHelper(getContext());    }    @Override    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {        super.onViewCreated(view, savedInstanceState);        args = getArguments();        if (args != null && args.getSerializable("MainActivityVM") != null) {            mainActivityVM = (MainActivityVM) args.getSerializable("MainActivityVM");            mainActivityVM.hideBackButton();        }        Button callNotification = view.findViewById(R.id.call_notification);        callNotification.setOnClickListener(v -> {            Intent intent = new Intent(getContext(), MainActivity.class);            mNotificationHelper.showNotification("Заголовок уведомления", "Текст уведомления", intent);        });    }    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup container,                             Bundle savedInstanceState) {        return inflater.inflate(R.layout.fragment_main, container, false);    }    @Override    public void onStop() {        super.onStop();        if (args != null) {            mainActivityVM.showBackButton();        }    }}